import { React, useState, useEffect } from 'react'
import { NavLink } from 'react-router-dom';
import PersonalProfile from './PersonalProfile';
import GameHistory from './GameHistory';

const Profile = () => {

  const [name, setName] = useState('No User')
  const [currentComponent, setComponent] = useState(2)
  const getProfile = async () => {
    const res = await fetch('/profile', {
      method: 'GET',
      headers: {
        "Content-type": 'application/json',
        Accept: 'application/json'
      },
      credentials: 'include'

    })

    
    


    const data = await res.json()
    console.log(data)
    setName(data.fullname)
  }

  const changeTab = (e) => {
    e.preventDefault()
    console.log('RUn')
    if (e.target.name == 'gh') setComponent(2)
    else if (e.target.name == 'pp') setComponent(1)
  }

  useEffect(() => {
    console.log('Profile page')
    getProfile();
  }, [])


  return (
    <div className='profle-main-div'>
      
      <nav>
  <div class="nav nav-tabs" id="nav-tab" role="tablist">
    <button class="nav-link active" id="nav-home-tab" data-bs-toggle="tab" data-bs-target="#nav-home" type="button" role="tab" aria-controls="nav-home" aria-selected="false">Home</button>
    <button class="nav-link" id="nav-profile-tab" data-bs-toggle="tab" data-bs-target="#nav-profile" type="button" role="tab" aria-controls="nav-profile" aria-selected="true">Profile</button>
    <button class="nav-link" id="nav-contact-tab" data-bs-toggle="tab" data-bs-target="#nav-contact" type="button" role="tab" aria-controls="nav-contact" aria-selected="false">Contact</button>
  </div>
</nav>
<div class="tab-content" id="nav-tabContent">
  <div class="tab-pane fade show active" id="nav-home" role="tabpanel" aria-labelledby="nav-home-tab">...</div>
  <div class="tab-pane fade" id="nav-profile" role="tabpanel" aria-labelledby="nav-profile-tab">...</div>
  <div class="tab-pane fade" id="nav-contact" role="tabpanel" aria-labelledby="nav-contact-tab">...</div>
</div>





      {(() => {
        switch (currentComponent) {
            case 1:
                return <PersonalProfile/>
            case 2:
                return <GameHistory></GameHistory>
            default :
                return <PersonalProfile/>
        }
    })()}
    
      
        
      
    </div>
  )
}

export default Profile
