import { React, useState, useEffect } from 'react'
import { NavLink } from 'react-router-dom';
import PersonalProfile from './PersonalProfile';
import GameHistory from './GameHistory';

const Profile = () => {

  const [name, setName] = useState('No User')
  const [currentComponent, setComponent] = useState(2)
  const getProfile = async () => {
    const res = await fetch('/profile', {
      method: 'GET',
      headers: {
        "Content-type": 'application/json',
        Accept: 'application/json'
      },
      credentials: 'include'

    })

    
    


    const data = await res.json()
    console.log(data)
    setName(data.fullname)
  }

  const changeTab = (e) => {
    e.preventDefault()
    print('RUn')
    if (e.name == 'gh') setComponent(2)
    else if (e.name == 'pp') setComponent(1)
  }

  useEffect(() => {
    console.log('Profile page')
    getProfile();
  }, [])


  return (
    <div className='profle-main-div'>
      
        
      {(() => {
        switch (currentComponent) {
            case 1:
                return <PersonalProfile/>
            case 2:
                return <GameHistory></GameHistory>
            default :
                return <PersonalProfile/>
        }
    })()}
    <button name = 'gh' onClick={changeTab}> Game </button>
    <button name = 'pp' onClick={changeTab}> Profile </button>
        
      <h2> {currentComponent}</h2>
      
        
      
    </div>
  )
}

export default Profile
